function ellipseText(str, count) {
  const prefix = count || 40;
  return str?.length > prefix ? str.slice(0, prefix) + "..." : str;
}

function getConfigListData(authConfig: any): any {
  const result = [];
  const { oauth2, basicAuth, apiKey, configKey, configValuePrefix } =
    authConfig;
  result.push({
    label: I18N("HEADER_KEY"),
    text: configKey || apiKey?.kv[0]?.key,
  });

  if (oauth2) {
    result.push(
      {
        label: I18N("HEADER_PREFIX"),
        text: configValuePrefix,
      },
      {
        label: I18N("ACCESS_TOKEN_URL"),
        text: oauth2.accessTokenUrl.length
          ? oauth2.accessTokenUrl
          : I18N("NOT_CONFIGURED"),
      },
      {
        label: I18N("EXPIRE_TIME"),
        text: oauth2.expireTime ?? I18N("NOT_CONFIGURED"),
      },
      {
        label: I18N("ACCESS_TOKEN_JSON_PATH"),
        text: oauth2.accessTokenJsonPath ?? "accessToken",
      }
    );
    result.unshift({ label: I18N("AUTH_METHOD"), text: "Oauth 2.0" });
  } else if (basicAuth) {
    result.push(
      {
        label: I18N("USERNAME"),
        text: basicAuth?.username ?? I18N("NOT_CONFIGURED"),
      },
      {
        label: I18N("PASSWORD"),
        text: basicAuth?.password ? I18N("CONFIGURED") : I18N("NOT_CONFIGURED"),
      }
    );
    result.unshift({ label: I18N("AUTH_METHOD"), text: " Basic Auth" });
  } else if (apiKey) {
    result.push({
      label: I18N("HEADER_VALUE"),
      text: apiKey.kv?.[0]?.value || I18N("NOT_CONFIGURED"),
    });

    result.unshift({ label: I18N("AUTH_METHOD"), text: "  API Key" });
  }

  return result;
}

function getParamsListData(configEnv: any): any {
  if (!Array.isArray(configEnv)) return [];
  return configEnv
    .map((item, index) => [
      { text: item.key, group: index },
      { text: item.value, group: index },
      { text: item?.description, group: index },
    ])
    .flat();
}

function getResponseData(detail: any): any {
  if (!detail) return { list: [] };
  const list = [];
  for (const [key, value] of Object.entries(detail)) {
    list.push({ key: key, type: typeof value, value: value });
  }
  return { list };
}

function processDataWithConfigEnv(data: any, configEnv: Array<any>): any {
  if (!configEnv?.length) return data;
  const replacePlaceholder = (s: string, map: any) => {
    return s.replace(/\$\{easyops-[a-zA-Z0-9_-]+\}/g, (str) => {
      return map[str.slice(10, -1)];
    });
  };

  const processItems = [
    "accessTokenBodyUrlEncoded",
    "accessTokenParams",
    "accessTokenHeader",
  ];
  const map = {};
  configEnv.map((item) => (map[item.key] = item.value));

  for (let key in data) {
    if (!processItems.includes(key)) continue;
    data[key] = (data[key] ?? []).map((item) => ({
      key: item.key,
      value: replacePlaceholder(item.value, map),
    }));
  }

  return data;
}

